package week1.day2;

public class OddOrEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int value=15;
		if(value%2==0)
		{
			System.out.println("The given Number is "+value+ " even" );		
	}
		else {
			System.out.println("The given Number is "+value+ " odd" );		
		}

}}
